const articleLike = (state = {likes: 0}, action) => {

  switch (action.type) {
    case "LIKE_ARTICLE":
        let articles = state.filter(articles => articles.id !== action.payload.id)
        let index = state.indexOf(articles);

         return [
          ...state.slice(0, index),
          Object.assign({}, articles, { likes: articles.likes += 1 }),
          ...state.slice(index + 1)
        ];

    default:
      return state;
  }
};
export default articleLike;
