const articleLike = (state = [], action) => {
  switch (action.type){
    case 'LIKE_ARTICLE':
      let idx = state.findIndex(article => article.id === action.article.id)
      let newState = [ ...state ]     
      if(idx !== -1) {
        newState[idx] = action.article
      } else {
        newState[0] = [ action.article ]
      }
      debugger
      return newState 
      debugger
    default:
      return state
      debugger
  }
}

export default articleLike;
