import {GET_ARTICLES} from './types';
import axios from 'axios';

const API_URL = "http://localhost:3000/api";

export function getArticles(){
  const request = axios.get(`{$API_URL}/articles`);

  return{
    type: GET_ARTICLES,
    payload: request
  }
}
