import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux'
import { fetchArticles, articleView } from '../actions/articleActions';
import { withRouter } from 'react-router-dom';
import Article from '../components/Article'



class Articles extends Component {

// When a user clicks the Add Name button, a name is added to the state and displayed on the page automatically
// Extra Credit - Add an input field where a user can input a name, click on Add Name, and that name is added to that state

	 constructor(props){
      	super(props)
     	 this.state = {
     	 	articles: [],
     	 	name: '',
     	 	names: ['Cernan', 'Avi', 'Connor', 'Leah'],
     	 }

  }

	componentDidMount() {
		this.props.fetchArticles();
	}

	componentWillReceiveProps(props){
		this.setState({
			articles: props.articles
		})
	}


	clickToSort = () => {

		let sortArticles = [...this.props.articles].sort(function (a, b) {

			 if (b.created_at < a.created_at) {
				    return -1;
				  }
			if (b.created_at > a.created_at) {
				    return 1;
				  }
			return 0;
			})

		this.setState({articles: [...sortArticles]})

	}

	filterByLikes = () => {
		let filterLike = this.state.articles.filter(article => article.likes > 0).sort(function(a, b) {	
		return a.likes - b.likes
			})

		this.setState({articles: [...filterLike]})			


 	}	


	selectArticle = (id) => {
		let filterView = this.state.articles.filter(article => article.id === id)
		this.props.articleView(filterView);
		this.props.history.push({pathname:'/articles/' + id});
	}

	 onClickaddName = () => {
	 	//let addName = this.state.names.map(name => name)
	 	 this.setState({
		      names: ['names']
		    });
	 	}

	 	 handleSubmit = event => {
	        event.preventDefault();
	 	 this.props.history.push('/articles') 
        }


	render(){
		let renderArticles;

		if (this.state.articles.length !== 0){
			renderArticles = this.state.articles.map(article => {
			return(
				<Link to={"/articles/" + article.id} key={article.id} >
					<Article
					title={article.title} 
					author={article.author}
					created_at={article.created_at}
					likes={article.likes} 
					image={article.image}
					clicked={() => this.selectArticle(article.id)} />
					<hr/>
				</Link>	
				);
			})
		} 	

		return(
			<div>
			 <form onSubmit={this.handleSubmit}>
		        <label>
		          Name:
		          <input type="text" value={this.state.value} onChange={this.handleChange} />
		        </label>
		        <input type="submit" value="Submit" />
		      </form>
              <br></br>
              <div>
              	<p> New User Name: {this.state.name}</p>
              </div>
              <br></br>
			  <div>
		        <button onClick={() => this.OnClickaddName()}> Add name from array</button>
		        	{this.state.names}
		        <br></br>
		        <br></br>
		      </div>

				<div>	
					<button className='btn btn-secondary' onClick={this.clickToSort}> 
					Sort by Most Recent </button> 
					  <br></br>
					  <br></br>
					 <button className='btn btn-secondary' onClick={this.filterByLikes}> Filter by Likes </button> 	
				</div>
				 <br></br>

				<div>
					{renderArticles} 
				</div>				
			</div>
		);
	}
};

const mapStateToProps = state => {
	return {
	  articles: state.allArticles.articles,
	}
  }


export default withRouter(connect(mapStateToProps, { fetchArticles, articleView })(Articles));
