import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Route } from 'react-router-dom';
import { deleteArticle } from '../actions/index';
import Articles from '../components/Articles'

const mapStateToProps = (state, ownProps) => {
  return ({ article: state.articles.find(article =>
    article.id === Number(ownProps.match.params.articleId))
  });
};

class ArticlesPage extends Component {
  render() {
    const {article, match, history, deleteArticle} = this.props;

    return (
      <div>
        <Route exact path={match.url} render={() => (
          <div>
            <Article
              title={article.title} 
              content={article.content}
              author={article.author}
              deleteArticle={deleteArticle}
              history={history}
             />
          </div>
        )} />
      </div>
    );
  };
};

export default connect(mapStateToProps, { deleteArticle })(ArticlesPage);
