  return (
    <div>
      <h1>{article.title}</h1>
        <p>{article.content}</p>
        <p> {article.author}</p>
            <button className='deletArticleButton'
              onClick={() => deleteArticle(article.id, history)}
            >
               DELETE ARTICLE!
            </button>
    </div>
  );
};

export default ArticlesPage;
