import React, { Component } from 'react';
import { connect } from 'react-redux';
import { deleteArticle, updateArticle } from '../actions/index';
import moment from "moment";



class ArticlesPage extends Component {

    handleClick = () => {
        this.props.deleteArticle(this.props.article.id);
        this.props.history.push({pathname:'/articles/'});
    }

    render(){


        return(
            <div class="articleshow">
                <h1 class="title"> {this.props.article.title}</h1>
                 <div>
                  <img 
                    class="image"
                    src={this.props.article.image}
                    alt={this.props.article.title}
                    />
                </div>

                <br></br>
                <div>
                    <p class = "content"> {this.props.article.content}</p>
                </div>
                <p> Author: {this.props.article.author}</p>
                <br></br>
                <div>
                    <h7> Date: { moment(this.props.article.created_at).format('MMMM Do YYYY') }</h7>
                </div>
                <br></br>
                <div class="field">
                        <p class="control">
                          <button class="button is-success" onClick={this.handleClick}>
                            Delete
                          </button>
                        </p>
                      </div>
            </div>
        )
    }
};
const mapStateToProps = state => {
    return{
       article: state.articleView
    }
}
export default connect(mapStateToProps, { deleteArticle, updateArticle })(ArticlesPage);
