import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux'
import { Route, NavLink, Switch } from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import { fetchArticle, articleView } from '../actions/index';
import Article from '../containers/Article'
import ArticlesPage from '../containers/ArticlesPage'



class Articles extends Component {

	componentDidMount() {
		this.props.fetchArticle()
	}

	articleSelectedHandler = (id) => {
		const { articles } = this.props;
		let filter = articles.filter(article => article.id === id)
		this.props.articleView(filter);
		this.props.history.push({pathname:'/articles/' + id});
	}

	render(){

		let { articles } = this.props
		let renderArticles;
		if (this.props && this.props.articles.length !== 0){
		renderArticles = articles.map(article => {
			return(
				<Link to={"/articles/" + article.id} key={article.id} >
					<Article
					title={article.title} 
					content={article.content}
					author={article.author}
					clicked={() => this.articleSelectedHandler(article.id)} />
				</Link>
			);
		})};

		return(
			<div>
                <header>
                    <nav>
                        <ul>
                            <li><NavLink to="/articles" exact>Articles</NavLink></li>
                            <li><NavLink to={{pathname: '/new-article'}}>Add Article</NavLink></li>
							<li><NavLink to="/login">Login</NavLink></li>
                        </ul>
                    </nav>
                </header>
                <Switch>
                    <Route path="/login" component={Login} />
					<Route path="/articles/:id" component={ArticlesPage} />
					<Route path="/goal" component={ChallengeTracker} />
                       <Route path="/new-article" render={(props) => (
                        <Suspense fallback={<div>Loading...</div>} >
                            <ArticleNew />
                        </Suspense>
                        )} />
						<section>
							{renderArticles}				
						</section> 
               </Switch>
			</div>
		);
	}

};
const mapStateToProps = state => {
	return {
	  articles: state.allArticles.articles,
	}
}

export default withRouter(connect(mapStateToProps, { fetchArticle, articleView })(Articles));
