import React, { Component, Suspense } from 'react';
import Article from '../containers/Article';
import { Link } from 'react-router-dom';
import ArticlesPage from '../containers/ArticlesPage';
import { Route, NavLink, Switch } from 'react-router-dom';
import Login from './userLogin';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux'
import { fetchArticle, viewArticle } from '../actions/index';
import ArticleNew from '../containers/ArticleNew';

class Articles extends Component {

	componentDidMount() {
		this.props.fetchArticle()
	}

	articleSelectedHandler = (id) => {
		let filter = this.props.articles.filter(article => article.id === id)
		this.props.viewArticle(filter);
		this.props.history.push({pathname:'/articles/' + id});
	}

	render(){
		const renderArticles = () => this.props.articles.map(article => {
			return(
			<Link to={"/articles/" + article.id} key={article.id} >
					<Articles
					title={article.title} 
					content={article.content}
					clicked={() => this.articleSelectedHandler(article.id)} />
			</Link>
			);
		});

		return(
			<div className="Challenge">
                <header>
                    <nav>
                        <ul>
                            <li><NavLink to="/articles" exact>Library</NavLink></li>
                            <li><NavLink to={{pathname: '/new-article'}}>Add Article</NavLink></li>
							<li><NavLink to="/login">Login</NavLink></li>
                        </ul>
                    </nav>
                </header>
                <Switch>
                    <Route path="/login" component={Login} />
					<Route path="/articles/:id" component={ArticlesPage} />
                       <Route path="/new-article" render={(props) => (
                        <Suspense fallback={<div>Loading...</div>} >
                            <ArticleNew />
                        </Suspense>
                        )} />
						<section>
							{renderArticles()}				
						</section> 
               </Switch>
			</div>
		);
	}
};
const mapStateToProps = state => {
	return {
	  articles: state.manageArticles.articles,
	}
  }

export default withRouter(connect(mapStateToProps, { fetchArticle, viewArticle })(Articles));
