import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Image from './Image';
import { connect } from 'react-redux';
import { likesArticle } from '../actions/index';

class ArticleLike extends Component {

  handleClick = () => {
    let currentArticle = this.props.article
    currentArticle.rating += 1;
    this.props.likesArticle(currentArticle);
   };

  render() {
    const { article, url } = this.props;
    let keyword = article.rating === 1 ? 'like' : 'likes';

    return(
      <div>
        <div className='listarticles'>
          <h2>{article.title}</h2>
          <Link
            to={`${url}/${article.id}`}
            style={{ textDecoration: 'none' }}
          >
            <Image
              imageURL={article.image.url}
              imageName={article.title}
              style={{width: 250, height: 250}}
            />
          </Link>
          <input type='button' value='Like me' onClick={this.handleClick.bind(this) } />
          <div style={{display: 'inline', marginLeft: 10}}>
            { article.rating} {keyword}
          </div>
        </div>
        <hr className='headerDivider' />
      </div>
    )
  }
};

export default connect(null, { likesArticle} )(ArticleLike);
