import React, { Component } from 'react';
import { connect } from 'react-redux';
import { deleteArticle, fetchArticle } from '../actions/index';



class ArticlesPage extends Component {

    componentWillMount(){
        this.props.fetchArticle(this.props.id);
    }

    handleClick = () => {
        this.props.deleteArticle(this.props.article[0].id);
        this.props.history.push({pathname:'/articles/'});
    }
    render(){
        return(
            <div class="articleshow">
                <h1 class="title"> {this.props.article.title}</h1>
                <h2 class = "content"> {this.props.article.content}</h2>
                <h2> Author: {this.props.article.author}</h2>
                <h5> Written: {this.props.article.created_at}</h5>
                <br></br>
                <div class="field">
                        <p class="control">
                          <button class="button is-success" onClick={this.handleClick}>
                            Delete
                          </button>
                        </p>
                      </div>
            </div>
        )
    }
};
const mapStateToProps = state => {
    return{
       article: state.articles.article
    }
}
export default connect(mapStateToProps, { deleteArticle, fetchArticle })(ArticlesPage);
