const initial_State= {
	articles: []
};

const allArticles = (state = initial_State, action) => {

	switch(action.type){
		case 'FETCH_ARTICLES':
			return{ ...state, articles: action.payload } 
		case 'ADD_ARTICLE':
        	return { ...state, articles: state.articles.concat(action.payload)}
      	case 'LIKE_ARTICLE': 
	     	return {...state, articles: state.articles.map(article => {
          		if (article._id === action.articleId) {
		            return {
		              ...article,
		              likes: !!article.likes ? article.likes.concat(action.payload) : [action.payload]
		            }
		          }
		          return article;
		        })
		      };
        case 'DELETE_ARTICLE':
        	{
		      const idx = state.findIndex(article => article.id === action.articleId);
		      return [...state.slice(0, idx), ...state.slice(idx + 1)]
		    }
		default:
			return state;
	}
}

  export default allArticles;
