const articleLike = (state = [], action) => {
  switch (action.type) {
    case 'LIKE_ARTICLE':
    	let article = state.find(article => article.id === action.articleId)
        let index = state.indexOf(article);
      return [
          ...state.slice(0, index),
          Object.assign({}, article, { likes: article.likes += 1 }),
          ...state.slice(index + 1)
        ];
    default:
      return state;

  }
}


export default articleLike
