const articleLike = (state = [], action) => {
  switch (action.type) {
    case 'LIKE_ARTICLE':{
      const index = state.findIndex(article => article.id === action.article.id);
      return [...state.slice(0, index), action.article, ...state.slice(index+1)]
    }
    default:
      return state;

  }
}


export default articleLike
