 const API_URL = 'http://localhost:3001/api';

export const login = (user) => {
  return {
    type: 'LOGIN',
    user

  }
};

 export const articleView = (payload) => {
    return {
      type: 'VIEW_ARTICLE',
      payload
    }
  }

export function likesArticle(articles){
  return {
    type: "LIKE_ARTICLE",
    articleId: articles
  }
}


export const fetchArticles = () => {
    let data = {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }
    }
    return dispatch => {
      fetch(`${ API_URL }/articles`, data)
        .then(response => response.json())
        .then(articles => {
          dispatch({
              type: 'FETCH_ARTICLES',
              payload: articles
          })
        })
        .catch(err => err)
    }
  }


  export const addArticle = (article) => {
    let data = {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(article)
    }
    return dispatch => {
      fetch(`${ API_URL }/articles`, data)
        .then(response => response.json())
        .then(article => dispatch({
          type: 'ADD_ARTICLE',
          payload: article
        }))
        .catch(err => err)
    }
  }

  export const deleteArticle = (id) => {
    let data = {
      method: 'DELETE',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    } 
    return dispatch => {
      fetch(`${ API_URL }/articles/${ id }`, data)
        .then(response => response.json())
        .then(article => dispatch({
          type: 'DELETE_ARTICLE',
          payload: article
        }))
        .catch(err => err)
    }
  }

